import React, { useCallback } from "react";
import s from "../SignIn/SignIn.module.scss";
import { Form, Formik, Field } from "formik";
import { connect } from "react-redux";
import { signUp } from "modules/auth/store/actions";
import { Button, TextField } from "@material-ui/core";
import { Link } from "react-router-dom";
import { routesByName } from "modules/routes/routesByName";

const initialValues = {
  login: "",
  password: "",
};

const SignUp = ({ signUp }) => {
  const handleSubmit = useCallback(
    async (values) => {
      await signUp(values);
    },
    [signUp]
  );

  return (
    <div className={s.root}>
      <div className={s.container}>
        <div className={s.header}>Регистрация</div>
        <div className={s.formContainer}>
          <Formik initialValues={initialValues} onSubmit={handleSubmit}>
            <Form className={s.formInner}>
              <Field
                name="login"
                placeholder="Логин"
                component={() => (
                  <TextField
                    id="standard-basic"
                    label="Логин"
                    className={s.field}
                  />
                )}
              />
              <Field
                name="password"
                placeholder="Пароль"
                component={() => (
                  <TextField
                    id="standard-basic"
                    label="Пароль"
                    className={s.field}
                  />
                )}
              />
              <Field
                name="passwordRepeat"
                placeholder="Повторите пароль"
                component={() => (
                  <TextField
                    id="standard-basic"
                    label="Пароль"
                    className={s.field}
                  />
                )}
              />
              <Button
                className={s.submitBtn}
                type="submit"
                variant="contained"
                color="primary"
              >
                Войти
              </Button>
            </Form>
          </Formik>
        </div>
        <div className={s.noAcc}>
          Нет аккаунта?{" "}
          <span>
            <Link to={routesByName.signUp}>Загеристрируйтесь</Link>
          </span>
        </div>
      </div>
    </div>
  );
};

export default connect(null, { signUp })(SignUp);
